INSERT INTO training (name, author, category, description,  image ) VALUES ( 'PicoCTF 2014', 'John Hammond', 'wargame', '<p>PicoCTF is a computer security game targeted at middle and high school students. The game consists of a series of challenges centered around a unique storyline where participants must reverse engineer, break, hack, decrypt, or do whatever it takes to solve the challenge. The challenges are all set up with the intent of being hacked, making it an excellent, legal way to get hands-on experience. </p><p>', '/static/training_pictures/picoctf_2014.png' );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Tyrannosaurus Hex', 'John Hammond', '    The contents of the flash drive appear to be password protected. On the back of the flash drive, you see the hexadecimal number 0x4355a5e6 scribbled in ink. The password prompt, however, only accepts decimal numbers. What number should you enter? ', '1129686502', 10, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'No Comment', 'John Hammond', "The CD you find has a copy of your father's website: homepage.html. Maybe something is hidden in the site...<br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Since this challenge only requires you to view the source, just submit this flag: <code>flag_f43cece2fb3bafc134c8056464abd793b9c3b8ce</code></span>", 'flag_f43cece2fb3bafc134c8056464abd793b9c3b8ce', 20, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Common Vulnerability Exercise', 'John Hammond', "This disc is encrypted. The surprisingly elaborate password hint refers to 'the CVE Identifier for a 2014 vulnerability that allowed arbitrary code execution in Firefox via a buffer overflow in a speech codec'. If you found this 'CVE-ID' thingy, it'd probably be the password. ", 'CVE-2014-1542', 20, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Caesar', 'John Hammond', "    You find an encrypted message written on the documents. Can you decrypt it? <br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Use this ciphertext: <code>qebpbzobqmxppmeoxpbfpmnezwrsraakxlqbguvdcgvkavseawh</code></span>    ", 'PQHCZUVUDDNAOTEJXYGFJYNDYVHDZK', 20, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'The Valley of Fear', 'John Hammond', 'The hard drive may be corrupted, but you were able to recover a small chunk of text. Scribbled on the back of the hard drive is a set of mysterious numbers. Can you discover the meaning behind these numbers? (1, 9, 4) (4, 2, 8) (4, 8, 3) (7, 1, 5) (8, 10, 1) ', 'Ceremonial plates', 20, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Internet Inspection', 'John Hammond', "On his computer, your father left open a browser with the Thyrin Lab Website. Can you find the hidden access code?<br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Since this challenge asks you to just view the page with developer tools, use this flag: <code>flag_e8f910e5f83893d2f7bc1229ca444cdc03be56d2</code></span>", 'flag_e8f910e5f83893d2f7bc1229ca444cdc03be56d2', 30, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Pickle Jar', 'John Hammond', 'The police station offers free pickles to police officers. However, someone stole the pickles from the pickle jar! You find a clue on a USB drive left at the scene of the crime.', 'YOUSTOLETHEPICKLES', 30, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'RoboPhoto', 'John Hammond', "Your father has been known to use the titles of his favorite books as passwords. While you don't remember any of the names of the books, your father keeps a poster for one of them on his wall. Can you figure out the name of the book and unlock the CD? ", 'The Positronic Man', 30, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'This is the Endian', 'John Hammond', "This is the end! Solving this challenge will help you defeat Daedalus's cyborg. You can find more information about endianness and the problem here. The flag is the smallest possible program input that causes the program to print 'Access Granted'.", 'cveR!edo', 40, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Intercepted Post', 'John Hammond', "We intercepted some of your Dad's web activity. Can you get a password from his traffic?. You can also view the traffic on CloudShark.", 'flag{pl$_$$l_y0ur_l0g1n_form$}', 40, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Supercow', 'John Hammond', 'Daedalus Corp. has a special utility for printing .cow files at /home/daedalus/supercow. Can you figure out how to get it to print out the flag?', 'cows_drive_mooooving_vans', 40, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Grep is Still Your Friend', 'John Hammond', "On his computer, your father left open a browser with the Thyrin Lab Website. Can you find the hidden access code?<br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Since this challenge asks you to just use grep, use this flag: <code>b2bee8664b754d0c85c4c0303134bca6</code></span>", 'b2bee8664b754d0c85c4c0303134bca6', 40, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Javascrypt', 'John Hammond', "Tyrin Robotics Lab uses a special web site to encode their secret messages. Can you determine the value of the secret key? <br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Use these values for the variable: <code>var i = 1; var x = 172; var n = 5493;</code></span>", 'flag_3855', 40, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Easy Overflow', 'John Hammond', "Is the sum of two positive integers always positive? <code>nc vuln2014.picoctf.com 50000</code> 'nc' is the Linux netcat command. Try running it in the shell.", 'That_was_easssy!', 40, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Write Right', 'John Hammond', 'Can you change the secret? The binary can be found at /home/write_right/ on the shell server.', 'arbitrary_write_is_always_right', 50, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Overflow 1', 'John Hammond', 'This problem has a buffer overflow vulnerability! Can you get a shell, then use that shell to read flag.txt? You can solve this problem interactively here, and the source can be found here. ', 'ooh_so_critical', 50, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Redacted', 'John Hammond', " You found <a href='https://2014.picoctf.com/problem-static/forensics/redacted/Redacted.pdf'>a letter</a> that may shed light on recent events. ", 'one_two_three_four', 50, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Toaster Control', 'John Hammond', "  Daedalus Corp. uses a <a href='http://web2014.picoctf.com/toaster-control-1040194/'>web interface</a> to control some of their toaster bots. It looks like they removed the command 'Shutdown & Turn Off' from the control panel. Maybe the functionality is still there... ", 'flag_c49bdkeekr5zqgvc20vc', 50, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'ZOR', 'John Hammond', "  Daedalus has encrypted their blueprints! Can you get us the password?  <br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. To submit the flag for this challenge, enter the MD5SUM of the decrypted message <i> up until the actual flag </i> (space included). </span>", '77b21e4a3d02a71f818991ccf392f3e5', 50, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Substitution', 'John Hammond', "  There's an authorization code for some Thyrin Labs information here, along with someone's favorite song. But it's been encrypted! Find the authorization code.  <br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Use the file downloaded here. </span>", 'awholenewworld', 50, 'picoctf_2014/substitution.txt', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Function Address', 'John Hammond', "  We found this program file on some systems. But we need the address of the 'find_string' function to do anything useful! Can you find it for us? ", '08048444', 60, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Basic ASM', 'John Hammond', "  We found this program, but we're having some trouble figuring it out. What's the value of %eax when the last instruction (the NOP) runs?   <br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Use the file downloaded here. </span>", '177651584', 60, 'picoctf_2014/basic_asm.txt', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Spoof Proof', 'John Hammond', "  The police have retrieved a network trace of some suspicious activity. Most of the traffic is users viewing their own profiles on a social networking website, but one of the users on the network downloaded a file from the Thyrin Labs VPN and spoofed their IP address in order to hide their identity. Can you figure out the last name of person that accessed the Thyrin files, and the two source IP addresses they used?[Example valid flag format: 'davis,192.168.50.6,192.168.50.7']", 'johnson,192.168.50.4,192.168.50.3', 60, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Delicious!', 'John Hammond', '  You have found the administrative control panel for the Daedalus Coperation Website: https://web2014.picoctf.com/delicious-5850932/login.php. Unfortunately, it requires that you be logged in. Can you find a way to convince the web site that you are, in fact, logged in?', 'session_cookies_are_the_most_delicious', 60, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Format', 'John Hammond', '  This program is vulnerable to a format string attack! See if you can modify a variable by supplying a format string! The binary can be found at /home/format/ on the shell server. The source can be found here. ', 'who_thought_%n_was_a_good_idea?', 70, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Repeated XOR', 'John Hammond', "  There's a secret passcode hidden in the robot's 'history of cryptography' module. But it's encrypted! Here it is, hex-encoded: encrypted.txt. Can you find the hidden passcode? <br><span style='color:red'> NOTE: This is automatically generated challenge, so the flag will be different from user to user. Use the file downloaded here. </span>", '1dd2a52a367b19748bba4ab6a53b03f1226da5bd', 70, 'picoctf_2014/repeated_xor.txt', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Overflow 2', 'John Hammond', '  This problem has a buffer overflow vulnerability! Can you get a shell? ', 'controlling_%eip_feels_great', 70, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Guess', 'John Hammond', '  This program requires you to guess a random 32-bit number! Sounds difficult, right? There is a server running at vuln2014.picoctf.com:4546, and the source can be found here. ', 'leak_the_seakret', 75, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Cyborg Secrets', 'John Hammond', '  You found a password protected binary on the cyborg relating to its defensive security systems. Find the password and get the shutdown code! You can find it on the shell server at /home/cyborgsecrets/cyborg-defense or you can download it here.', '403-shutdown-for-what', 80, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Droid App', 'John Hammond', "  An Android application was released for the toaster bots, but it seems like this one is some sort of debug version. Can you discover the presence of any debug information being stored, so we can plug this?You can download the apk <a href='https://2014.picoctf.com/problem-static/forensics/DroidApp/ToasterBot.apk'>here</a>. ", 'what_does_the_logcat_say', 80, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Snapcat', 'John Hammond', "  It was found that a Daedalus employee was storing his personal files on a work computer. Unfortunately, he corrupted the filesystem before we could prove it. Can you take a look? Download <a href='https://2014.picoctf.com/problem-static/forensics/snapcat/disk.img'>here</a>. ", 'i_can_has_cheezburger', 80, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'RSA', 'John Hammond', "  A Daedalus Corp spy sent an RSA-encrypted message. We got their key data, but we're not very good at math. Can you decrypt it? Here's the data. Note that the flag is not a number but a number decoded as ASCII text.", 'modular_arithmetics_not_so_bad_after_all!', 80, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'ExecuteMe', 'John Hammond', '  This program will run whatever you send to it! Try to get the flag! The binary can be found at /home/execute/ on the shell server. The source can be found here.', 'shellcode_is_kinda_cool', 80, '', '', (SELECT id FROM training WHERE name = 'PicoCTF 2014') );
INSERT INTO training (name, author, category, description, image ) VALUES ( 'OverTheWire - Bandit', 'John Hammond', 'wargame', '<p>The Bandit wargame is aimed at absolute beginners. It will teach the basics needed to be able to play other wargames.</p><p>This game, like most other games, is organised in levels. You start at Level 0 and try to "beat" or "finish" it. Finishing a level results in information on how to start the next level. The pages on this website for "Level <X>" contain information on how to start level X from the previous level. E.g. The page for Level 1 has information on how to gain access from Level 0 to Level 1.</p><p>You will encounter many situations in which you have no idea what you are supposed to do. <b>Dont panic! Dont give up!</b> The purpose of this game is for you to learn the basics. Part of learning the basics, is reading a lot of new information.</p>', '/static/training_pictures/overthewire.png' );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 0', 'John Hammond', 'The goal of this level is for you to log into the game using SSH. The host to which you need to connect is <b>bandit.labs.overthewire.org</b>, on port <code>2220</code>. The username is <code>bandit0</code> and the password is <code>bandit0</code>. <br><br>The password for the next level is stored in a file called readme located in the home directory. Use this password to log into bandit1 using SSH. Whenever you find a password for a level, use SSH (on port 2220) to log into that level and continue the game.', 'gtVrDuiDfck831PqWsLEZy5gyDz1clto', 5, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 1', 'John Hammond', 'The password for the next level is stored in a file called - located in the home directory', 'ZluruAthQk7Q2MqmDeTiUij2ZvWy2mBi', 5, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 2', 'John Hammond', 'The password for the next level is stored in a file called spaces in this filename located in the home directory', 'sJIJNW6ucpu6HPZ1ZAchaDtwd7oGrD14', 5, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 3', 'John Hammond', '    The password for the next level is stored in a hidden file in the inhere directory.', 'Z9tkRkWmpt9Qr7XrR5jWRkgOU901swEZ', 5, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 4', 'John Hammond', 'The password for the next level is stored in the only human-readable file in the inhere directory. Tip: if your terminal is messed up, try the "reset" command.', 'iX6IOfmpN7AYOQGPwtn3fXpbaJVJcHfq', 10, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 5', 'John Hammond', 'The password for the next level is stored in a file somewhere under the inhere directory and has all of the following properties: - human-readable - 1033 bytes in size - not executable', 'aGoY4q2Dc6MgDq4oL4YtoKtyAg9PeHa1', 10, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 6', 'John Hammond', 'The password for the next level is stored somewhere on the server and has all of the following properties: - owned by user bandit7 - owned by group bandit6 - 33 bytes in size', '7z3hEENjQtflzgnT29q7wAvMNfZdh0i9', 10, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 7', 'John Hammond', 'The password for the next level is stored in the file data.txt next to the word millionth', 'DBfUBfqQG69KvJvJ1iAbMoIpwSNQ9bWe', 10, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 8', 'John Hammond', 'The password for the next level is stored in the file data.txt and is the only line of text that occurs only once', 'UsvVyFSfZZWbi6wgC7dAFyFuR6jQQUhR', 10, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 9', 'John Hammond', "The password for the next level is stored in the file data.txt in one of the few human-readable strings, beginning with several '=' characters.", 'truKLdjsbJ5g7yyJ2X2R0o3a5HQJFuLk', 15, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 10', 'John Hammond', 'The password for the next level is stored in the file data.txt, which contains base64 encoded data', 'IFukwKGsFW8MOq3IRFqrxE1hxTNEbUPR', 15, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 11', 'John Hammond', 'The password for the next level is stored in the file data.txt, where all lowercase (a-z) and uppercase (A-Z) letters have been rotated by 13 positions', '5Te8Y4drgCRfCx8ugdwuEX8KFC6k2EUu', 15, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 12', 'John Hammond', 'The password for the next level is stored in the file data.txt, which is a hexdump of a file that has been repeatedly compressed. For this level it may be useful to create a directory under /tmp in which you can work using mkdir. For example: mkdir /tmp/myname123. Then copy the datafile using cp, and rename it using mv (read the manpages!)', '8ZjyCRiBWFYkneahHwxCv3wb2a1ORpYL', 15, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 13', 'John Hammond', "The password for the next level is stored in /etc/bandit_pass/bandit14 and can only be read by user bandit14. For this level, you don't get the next password, but you get a private SSH key that can be used to log into the next level. Note: localhost is a hostname that refers to the machine you are working on", '4wcYUJFw0k0XLShlDzztnTBHiqxU3b3e', 15, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 14', 'John Hammond', 'The password for the next level can be retrieved by submitting the password of the current level to port 30000 on localhost.', 'BfMYroe26WYalil77FoDi9qh59eK5xNr', 20, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 15', 'John Hammond', '    The password for the next level can be retrieved by submitting the password of the current level to port 30001 on localhost using SSL encryption. <br>    Helpful note: Getting "HEARTBEATING" and "Read R BLOCK"? Use -ign_eof and read the "CONNECTED COMMANDS" section in the manpage. Next to R and Q, the B command also works in this version of that command...', 'cluFn7wTiGryunymYOu4RcffSxQluehd', 20, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 16', 'John Hammond', "The credentials for the next level can be retrieved by submitting the password of the current level to a port on localhost in the range 31000 to 32000. First find out which of these ports have a server listening on them. Then find out which of those speak SSL and which don't. There is only 1 server that will give the next credentials, the others will simply send back to you whatever you send to it. <br><span style='color:red'>To submit this flag for the USCGA Cyber Team, submit the <code>md5sum</code> of the obtained private key file. </span>", '571163e61081adffbc20b27d17647cf0', 20, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 17', 'John Hammond', "    There are 2 files in the homedirectory: passwords.old and passwords.new. The password for the next level is in passwords.new and is the only line that has been changed between passwords.old and passwords.new <br>    NOTE: if you have solved this level and see 'Byebye!' when trying to log into bandit18, this is related to the next level, bandit19 ", 'kfBf3eYk5BPBRzwjqutbbfE887SVc5Yd', 20, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 18', 'John Hammond', 'The password for the next level is stored in a file readme in the homedirectory. Unfortunately, someone has modified .bashrc to log you out when you log in with SSH.', 'IueksS7Ubh8G3DCwVzrTd8rAVOwq3M5x', 20, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 19', 'John Hammond', 'To gain access to the next level, you should use the setuid binary in the homedirectory. Execute it without arguments to find out how to use it. The password for this level can be found in the usual place (/etc/bandit_pass), after you have used the setuid binary.', 'GbKksEFF4yrVs6il55v6gwY5aVje5f0j', 25, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 20', 'John Hammond', '    There is a setuid binary in the homedirectory that does the following: it makes a connection to localhost on the port you specify as a commandline argument. It then reads a line of text from the connection and compares it to the password in the previous level (bandit20). If the password is correct, it will transmit the password for the next level (bandit21).   <br>    NOTE: To beat this level, you need to login twice: once to run the setuid command, and once to start a network daemon to which the setuid will connect.<br>    NOTE 2: Try connecting to your own network daemon to see if it works as you think', 'gE269g2h3mw3pwgrj0Ha9Uoqen1c9DGr', 25, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 21', 'John Hammond', 'A program is running automatically at regular intervals from cron, the time-based job scheduler. Look in /etc/cron.d/ for the configuration and see what command is being executed.', 'Yk7owGAcWjwMVRwrTesJEwB7WVOiILLI', 25, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 22', 'John Hammond', '    A program is running automatically at regular intervals from cron, the time-based job scheduler. Look in /etc/cron.d/ for the configuration and see what command is being executed.    <br>    NOTE: Looking at shell scripts written by other people is a very useful skill. The script for this level is intentionally made easy to read. If you are having problems understanding what it does, try executing it to see the debug information it prints.', 'jc1udXuA1tiHqjIsL8yaapX5XIAI6i0n', 25, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 23', 'John Hammond', '    A program is running automatically at regular intervals from cron, the time-based job scheduler. Look in /etc/cron.d/ for the configuration and see what command is being executed.    <br>    NOTE: This level requires you to create your own first shell-script. This is a very big step and you should be proud of yourself when you beat this level!    <br>    NOTE 2: Keep in mind that your shell script is removed once executed, so you may want to keep a copy around...', 'UoMYTrfrBFHyQXmg6gzctqAwOmw1IohZ', 35, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 24', 'John Hammond', 'A daemon is listening on port 30002 and will give you the password for bandit25 if given the password for bandit24 and a secret numeric 4-digit pincode. There is no way to retrieve the pincode except by going through all of the 10000 combinations, called brute-forcing.', 'uNG9O58gUE7snukf3bvZ0rxhtnjzSGzG', 35, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO challenges (name, author, prompt, possible_answers, points, downloadable_files, hint, association) VALUES ( 'Bandit - Level 25', 'John Hammond', 'Logging in to bandit26 from bandit25 should be fairly easy... The shell for user bandit26 is not /bin/bash, but something else. Find out what it is, how it works and how to break out of it.', '5czgV9L3Xx8JPOyRbXh6lQbmIOWvPT6Z', 35, '', '', (SELECT id FROM training WHERE name = 'OverTheWire - Bandit') );
INSERT INTO training (name, author, category, description,  image ) VALUES ( 'OverTheWire - Natas', 'John Hammond', 'wargame', "<p> Natas teaches the basics of server-side web-security.</p><p>Each level of natas consists of its own website located at http://natasX.natas.labs.overthewire.org, where X is the level number. There is no SSH login. To access a level, enter the username for that level (e.g. natas0 for level 0) and its password.</p><p>Each level has access to the password of the next level. Your job is to somehow obtain that next password and level up. All passwords are also stored in <code>/etc/natas_webpass/</code>. E.g. the password for natas5 is stored in the file <code>/etc/natas_webpass/natas5</code> and only readable by natas4 and natas5.</p><p><b> Start here: </b> Username: <code>natas0</code> Password: <code>natas0</code> URL: <a href='http://natas0.natas.labs.overthewire.org'>http://natas0.natas.labs.overthewire.org</a></p>", '/static/training_pictures/overthewire.png' );
